class DwarfPlanet {
  final String id;
  final String name;
  final String location;
  final String distance;
  final String orbit;
  final String gravity;
  final String description;
  final String image;
  final String picture;

  const DwarfPlanet ({this.id, this.name, this.location, this.distance, this.orbit, this.gravity, this.description, this.image, this.picture});
}

